Type":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"_depositor","type":"address"}],"name":"pendingPayoutFor","outputs":[{"internalType":"uint256","name":"pendingPayout_","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"_depositor","type":"address"}],"name":"percentVestedFor","outputs":[{"internalType":"uint256","name":"percentVested_","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"priceRate","outputs":[{"internalType":"uint256","name":"rate_","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"principle","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"pullManagement","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"newOwner_","type":"address"}],"name":"pushManagement","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"_token","type":"address"}],"name":"recoverLostToken","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"_recipient","type":"address"},{"internalType":"bool","name":"_stake","type":"bool"}],"name":"redeem","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"renounceManagement","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"bool","name":"_addition","type":"bool"},{"internalType":"uint256","name":"_increment","type":"uint256"},{"internalType":"uint256","name":"_target","type":"uint256"},{"internalType":"uint256","name":"_buffer","type":"uint256"}],"name":"setAdjustment","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"enum BondDepository.PARAMETER","name":"_parameter","type":"uint8"},{"internalType":"uint256","name":"_input","type":"uint256"}],"name":"setBondTerms","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"_staking","type":"address"}],"name":"setStaking","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"staking","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"terms","outputs":[{"internalType":"uint256","name":"controlVariable","type":"uint256"},{"internalType":"uint256","name":"vestingTerm","type":"uint256"},{"internalType":"uint256","name":"minimumPriceRate","type":"uint256"},{"internalType":"uint256","name":"maxPayout","type":"uint256"},{"internalType":"uint256","name":"fee","type":"uint256"},{"internalType":"uint256","name":"maxDebt","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"totalDebt","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"treasury","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"}],"devdoc":{"kind":"dev","methods":{"assetPrice()":{"returns":{"_0":"uint256 in 10 ** 6 precision"}},"bondPrice()":{"returns":{"price_":"uint256 in 10**6 precision in usd"}},"currentDebt()":{"returns":{"_0":"uint256 in 10 ** 18 precision"}},"debtDecay()":{"returns":{"decay_":"uint256"}},"debtRatio()":{"returns":{"debtRatio_":"uint256 in 10 ** 9 precision"}},"deposit(uint256,uint256,address)":{"params":{"_amount":"uint256 in 10 ** 18 precision","_depositor":"address","_maxPrice":"uint256"},"returns":{"_0":"uint256"}},"initializeBondTerms(uint256,uint256,uint256,uint256,uint256,uint256,uint256)":{"params":{"_controlVariable":"uint256","_fee":"uint256","_initialDebt":"uint256","_maxDebt":"uint256","_maxPayout":"uint256","_minimumPriceRate":"uint256","_vestingTerm":"uint256"}},"kbtPrice()":{"returns":{"_0":"uint256 in 10**6 precision"}},"maxPayout()":{"returns":{"_0":"uint256"}},"payoutFor(uint256)":{"params":{"_value":"uint256 10**18 precision"},"returns":{"_0":"uint256 10**18 precision"}},"pendingPayoutFor(address)":{"params":{"_depositor":"address"},"returns":{"pendingPayout_":"uint256"}},"percentVestedFor(address)":{"params":{"_depositor":"address"},"returns":{"percentVested_":"uint256"}},"priceRate()":{"returns":{"rate_":"uint256 in 10**9 precision"}},"recoverLostToken(address)":{"returns":{"_0":"bool"}},"redeem(address,bool)":{"params":{"_recipient":"address","_stake":"bool"},"returns":{"_0":"uint256"}},"setAdjustment(bool,uint256,uint256,uint256)":{"params":{"_addition":"bool","_buffer":"uint256","_increment":"uint256","_target":"uint256"}},"setBondTerms(uint8,uint256)":{"params":{"_input":"uint256","_parameter":"PARAMETER"}},"setStaking(address)":{"params":{"_staking":"address"}}},"version":1},"userdoc":{"kind":"user","methods":{"assetPrice()":{"notice":"get asset price from klaybank oracle"},"bondPrice()":{"notice":"calculate current bond premium"},"currentDebt()":{"notice":"calculate debt factoring in decay"},"debtDecay()":{"notice":"amount to decay total debt by"},"debtRatio()":{"notice":"calculate current ratio of debt to KBT supply"},"deposit(uint256,uint256,address)":{"notice":"deposit bond"},"initializeBondTerms(uint256,uint256,uint256,uint256,uint256,uint256,uint256)":{"notice":"initializes bond parameters"},"kbtPrice()":{"notice":"returns kbt price in usd"},"maxPayout()":{"notice":"determine maximum bond size"},"payoutFor(uint256)":{"notice":"calculate interest due for new bond"},"pendingPayoutFor(address)":{"notice":"calculate amount of KBT available for claim by depositor"},"percentVestedFor(address)":{"notice":"calculate how far into vesting a depositor is"},"priceRate()":{"notice":"calculate bond price rate"},"recoverLostToken(address)":{"